mapper.select:
SELECT SALE_NO, SALE_NAME, SALE_ADDR, SALE_PHONE, SALE_COMMENT, SALE_DATE, SALE_PRICE, SALE_STATE, CUSTOMER_NO
FROM SALE
WHERE ${option} = #{value}

mapper.update:
UPDATE SALE
SET SALE_STATE = #{saleState}
WHERE SALE_NO = #{saleNo}

mapper.select:
SELECT COUNT(*)
FROM SALE
 WHERE CUSTOMER_NO = #{customerNo}

mapper.update:
UPDATE SALE
SET SALE_STATE = #{state}
WHERE SALE_NO = #{saleNo}
